import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Container, PictureWrapper, ProductInfo, SellerInfo } from "./styles";

function DetailsPage() {
  const [productData, setProductData] = useState({});
  const { productId } = useParams();

  useEffect(() => {
    fetch(`http://localhost:3000/product/consult?id=${productId}`, {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    }).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 404:
          console.log("Produto não encontrado");
          break;
        case 200:
          setProductData(data);
          break;
      }
    });
  }, []);

  return (
    <Container>
      <PictureWrapper></PictureWrapper>
      <ProductInfo>
        <h2>{productData.name}</h2>
        <p>{productData.rating}</p>
        <h1>R$ {productData.price}</h1>
        <h3>{`em ${productData.portion}x R$${(productData.price / productData.portion).toFixed(
          2
        )}`}</h3>
      </ProductInfo>
      <SellerInfo></SellerInfo>
    </Container>
  );
}

export default DetailsPage;
