import styled from 'styled-components';

export const Container = styled.div`
  width: 200px;
  min-height: 300px;
  background-color: gray;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  margin: 20px;
  align-items: flex-start;

  img {
      width: 100%;
  }
`;

export const ProductInfo = styled.div`
    display: flex;
    flex-direction: column;
    padding: 10px;
`

