import React, { useState } from "react";

import { Container, LoginWrapper, LoginInput } from "./styles";

function UserLogin() {
  const [userEmail, setUserEmail] = useState("");
  const [userPass, setUserPass] = useState("");

  function authenticateUser() {
    fetch("http://localhost:3000/auth/authenticate", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        email: userEmail,
        password: userPass,
      }),
    })
      .then((res) => {
        console.log(res.status);

        switch (res.status) {
          case 404:
            console.log("Usu치rio n칚o encontrado");
            break;
          case 403:
            console.log("Senha inv치lida");
            break;
          case 200:
            let data = res.json();
            alert(data.check_user.name);
            break;
        }
      })
      .then((response) => {
        alert(response.check_user.name);
      });
  }

  return (
    <Container>
      <LoginWrapper>
        <h2>Ol치 游녦, digite seu email e sua senha cadastrada!</h2>
        <p>Email</p>
        <LoginInput
          type="text"
          onChange={(e) => setUserEmail(e.target.value)}
        />
        <p>Senha</p>
        <LoginInput
          type="password"
          onChange={(e) => setUserPass(e.target.value)}
        />
        <LoginInput
          type="button"
          value="Entrar"
          onClick={() => authenticateUser()}
        />
      </LoginWrapper>
    </Container>
  );
}

export default UserLogin;
