import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Container, LoginWrapper, LoginInput } from "./styles";
import {Redirect} from 'react-router-dom'

function UserLogin() {
  const [userEmail, setUserEmail] = useState("");
  const [userPass, setUserPass] = useState("");
  const dispatch = useDispatch();

  function authenticateUser() {
    fetch("http://localhost:3000/auth/authenticate", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        email: userEmail,
        password: userPass,
      }),
    }).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 404:
          console.log("Usu치rio n칚o encontrado");
          break;
        case 403:
          console.log("Senha inv치lida");
          break;
        case 200:
          if (data.token) {
            userLogin(data.check_user.name, data.token);
            return <Redirect to="/" />
          } else {
            alert('fucking error')
          }
          break; 
      }
    });
  }

  function userLogin(username, token) {
    localStorage.setItem('access_token', token);
    localStorage.setItem('logged_username', username)
  }

  return (
    <Container>
      <LoginWrapper>
        <h2>Ol치 游녦, digite seu email e sua senha cadastrada!</h2>
        <p>Email</p>
        <LoginInput
          type="text"
          onChange={(e) => setUserEmail(e.target.value)}
        />
        <p>Senha</p>
        <LoginInput
          type="password"
          onChange={(e) => setUserPass(e.target.value)}
        />
        <LoginInput
          type="button"
          value="Entrar"
          onClick={() => authenticateUser()}
        />
      </LoginWrapper>
    </Container>
  );
}

export default UserLogin;
