import React, { useEffect, useState } from "react";

import { Container } from "./styles";

function CartPage() {
  const [cartData, setCartData] = useState({ ProductData: [], CartData: [] });

  useEffect(() => {
    fetch("http://localhost:3000/cart/consultTheCart", {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${localStorage.getItem("access_token")}`,
      },
    }).then(async (res) => {

      let data = await res.json();

      res.data.cart.forEach((cartItem) => {
        res.data.product.forEach((productDetail, i) => {
          if (cartItem.id === productDetail._id) {
            res.data[i].quantity = cartItem.quantity;
          }
        });
      });

      switch (res.status) {
        case 200:
          setCartData({ data: data });
          break;
      }
    });
  }, []);

  return <Container>{cartData.cart.map((p) => {})}</Container>;
}

export default CartPage;
