  height: 20px;
  width: 20px;
  fill: rgba(0, 0, 0, 0.5);
  cursor: pointer;
`;
export const SearchBar = styled.form`
`;

export const Bar = styled.input`
  background-color: white;
  padding: 10px;
  outline: none;
  border: 0;
  border-radius: 5px;
`;

export const Row = styled.div`
  display: flex;
  flex-direction: row;
`;

export const UserInfo = styled.div`
  display: flex;
  flex-direction: column;
  font-size: 11pt;

  p {
    overflow: hidden;
