import styled from "styled-components";

export const Container = styled.div``;

export const ProductRegisterWrapper = styled.div`
  display: grid;
  padding: 26px;
  border-radius: 12px;
  max-width: 1300px;
  background-color: white;
  margin: 20px auto;
  grid-template-columns: 500px auto 320px;
  grid-template-rows: 470px auto auto;
  grid-template-areas:
    "picture productinfo sellerinfo"
    "productdescription productdescription sellerinfo"
    "recommended recommended .";

  @media (max-width: 1000px) {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  }
`;

export const ProductRegisterInput = styled.input`
  width: 100%;
  padding: 0.5rem;
  margin: 10px;
  border-radius: 12px;
  outline: none;
  background-color: transparent;
  border: 2px solid var(--accent-color);
`;
