import styled from "styled-components";

export const Container = styled.div``;
export const CartContainer = styled.div`
  width: 90%;
  display: flex;
  flex-direction: row;
  margin: 10px;
  background-color: white;
  padding: 16px;
  border-radius: 12px;

  img {
    width: 80px;
    height: 80px;
  }
`;

export const Info = styled.div`
  display: flex;
  flex-direction: column;
  margin: 0 20px;

  p {
    overflow-x: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    width: 200px;
  }
`;
