import React, { useState } from "react";

import { Container, LoginWrapper, LoginInput } from "./styles";

function UserLogin() {
  const [userEmail, setUserEmail] = useState("");
  const [userPass, setUserPass] = useState("");

  function authenticateUser() {
    fetch("http://localhost:3000/auth/authenticate", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        email: userEmail,
        password: userPass,
      }),
    })
      .then( async (res) => {
        let data = await res.json();

        if (res.status === 404) {
            console.log('go')
        }

        switch (res.status) {
            case 200:
                console.log('foda')
                break;
            case 404:
                console.log('foda mais')
                break;
            case 403:
                console.log('foda mt mais')
                break;
        }

      })
  }

  return (
    <Container>
      <LoginWrapper>
        <h2>OlÃ¡ ðŸ‘‹, digite seu email e sua senha cadastrada!</h2>
        <p>Email</p>
        <LoginInput
          type="text"
          onChange={(e) => setUserEmail(e.target.value)}
        />
        <p>Senha</p>
        <LoginInput
          type="password"
          onChange={(e) => setUserPass(e.target.value)}
        />
        <LoginInput
          type="button"
          value="Entrar"
          onClick={() => authenticateUser()}
        />
      </LoginWrapper>
    </Container>
  );
}

export default UserLogin;
