import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 20px 0;
`;
export const CartContainer = styled.div`
  width: 90%;
  display: flex;
  flex-direction: row;
  margin: 5px;
  background-color: white;
  padding: 16px;
  border-radius: 12px;

  img {
    width: 80px;
    height: 80px;
  }

  select {
    margin: 0 10px;
    padding: 5px;
    height: 40px;
    border-radius: 5px;
    border: none;
    outline: none;
  }
`;

export const Info = styled.div`
  display: flex;
  flex-direction: column;
  margin: 0 20px;

  p {
    width: 80%;
    overflow: hidden;
    text-overflow: ellipsis;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
`;
