import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Rating from "@material-ui/lab/Rating";
import {
  Container,
  PictureWrapper,
  ProductInfo,
  SellerInfoWrapper,
  SellerInfo,
  Picture,
} from "./styles";

function DetailsPage() {
  const [productData, setProductData] = useState({});
  const { productId } = useParams();

  useEffect(() => {
    fetch(`http://localhost:3000/product/consult?id=${productId}`, {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    }).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 404:
          console.log("Produto não encontrado");
          break;
        case 200:
          setProductData(data);
          break;
      }
    });
  }, []);

  function calculateReview() {
    let soma2 = 0;
    if (productData.review != undefined) {
      var soma = productData.review.reduce(function (soma, i) {
        return soma + i;
      });

      soma2 = soma;
    }
    alert(soma2)
  }

  return (
    <Container>
      <PictureWrapper>
        <Picture src={productData.image} />
      </PictureWrapper>
      <ProductInfo>
        <h2>{productData.name}</h2>
        <Rating name="half-rating" defaultValue={calculateReview()} />
        <h1>R$ {productData.price}</h1>
        <h4>{`em ${productData.portion}x R$${(
          productData.price / productData.portion
        ).toFixed(2)}`}</h4>
        <hr />
        <span>
          <strong>Marca: </strong>
          <p>{productData.brand}</p>
        </span>
      </ProductInfo>
      <SellerInfoWrapper>
        <SellerInfo></SellerInfo>
      </SellerInfoWrapper>
    </Container>
  );
}

export default DetailsPage;
