import React, { useEffect, useState } from "react";

import { Container, CartContainer, Info } from "./styles";

function CartCard(props) {
  return (
    <CartContainer>
      <img src={props.image}></img>
      <Info>
        <p>{props.title}</p>
        <sub>R$ {props.price} em {props.portion}</sub>
      </Info>
    </CartContainer>
  );
}

function CartPage() {
  const [cartData, setCartData] = useState({ data: [] });

  useEffect(() => {
    fetch("http://localhost:3000/cart/consultTheCart", {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${localStorage.getItem("access_token")}`,
      },
    }).then(async (res) => {
      let data = await res.json();

      data.product.forEach((item) => {
        data.cart.forEach((cartItem) => {
          if (item._id === cartItem.id) {
            item.quantity = cartItem.quantity;
          }
        });
      });

      switch (res.status) {
        case 200:
          setCartData({ data: data.product });
          break;
      }
    });
  }, []);

  return (
    <Container>
      {cartData.data.map((p) => {
        return <CartCard image={p.image} title={p.name} price={p.price} />;
      })}
    </Container>
  );
}

export default CartPage;
