import React, { useEffect, useState } from "react";
import Rating from "@material-ui/lab/Rating";
import { Container, CartContainer, Info, Row, StockInfo } from "./styles";

function CartCard(props) {
  const [buyQuantity, setBuyQuantity] = useState(1);
  const [valid, setValid] = useState(true);

  function removeFromCart() {
    fetch(`http://localhost:3000/cart/removeFromCart?id=${props.id}`, {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${localStorage.getItem("access_token")}`,
      },
    }).then(async (res) => {
      let data = await res.json();
    });
  }

  return (
    <CartContainer>
      {valid ? <><Row>
        <img src={props.image}></img>
        <Info>
          <p>{props.title}</p>
          <sub>
            R$ {props.price} em {props.portion}x de {"R$"}
            {(props.price / props.portion).toFixed(2)}
          </sub>
          <Rating
            name="half-rating"
            defaultValue={props.rating}
            size="small"
            precision={0.5}
            readOnly
          />
          <b>
            R$ {props.deliveryPrice} para {props.cep}
          </b>
        </Info>
      </Row>
      <StockInfo>
        <input
          type="button"
          value="daohra hein"
          onClick={() => removeFromCart()}
        ></input>
        <select
          onChange={(e) => setBuyQuantity(e.target.value)}
          selected={props.quantity}
        >
          {Array.from(Array(props.countInStock).keys()).map((i) => {
            return i > 0 && i < 6 && <option value={i}>{i}</option>;
          })}
        </select>
        <sub>{props.countInStock} disponiveis</sub>
      </StockInfo></> : <h1>Não valido</h1>}

    </CartContainer>
  );
}

function CartPage() {
  const [cartData, setCartData] = useState({ data: [] });
  const [userData, setUserData] = useState({});

  useEffect(() => {
    fetch("http://localhost:3000/cart/consultTheCart", {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${localStorage.getItem("access_token")}`,
      },
    }).then(async (res) => {
      let data = await res.json();

      data.product.forEach((item) => {
        data.cart.forEach((cartItem) => {
          if (item._id === cartItem.id) {
            item.quantity = cartItem.quantity;
          }
        });
      });

      switch (res.status) {
        case 200:
          setCartData({ data: data.product });
          break;
      }
    });
  }, []);

  useEffect(() => {
    fetch("http://localhost:3000/auth/consult", {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${localStorage.getItem("access_token")}`,
      },
    }).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 200:
          setUserData(data);
          break;
      }
    });
  }, []);

  return (
    <Container>
      {cartData.data.map((p) => {
        return (
          <CartCard
            image={p.image}
            title={p.name}
            price={p.price}
            portion={p.portion}
            rating={p.rating}
            deliveryPrice={p.deliveryPrice}
            quantity={p.quantity}
            countInStock={p.countInStock}
            id={p._id}
            cep={userData.cep}
          />
        );
      })}
    </Container>
  );
}

export default CartPage;
