import React, { useEffect, useState } from "react";
import ProductCard from "../../components/ProductCard";
import Axios from "axios";
import { Container } from "./styles";

function MainPage() {
  const [products, setProducts] = useState({data: []});
  useEffect(() => {
    fetch('http://localhost:3000/product/consult').then(res => res.json()).then((result) => {
        setProducts({data: result})
    })
  }, []);

  return (
    <Container>
      {products.data.map((p) => {
        return (
          <ProductCard key={p.id} imgsrc={p.image} title={p.name} brand={p.brand} price={`R$ ${p.price}`} portion={`${p.portion}x ${(p.price / p.portion).toFixed(1)}`} />
        );
      })}
    </Container>
  );
}

export default MainPage;
