import styled from 'styled-components';

export const Container = styled.div`
  width: 200px;
  height: 300px;
  background-color: gray;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  margin: 20px;
  align-items: center;

  img {
      width: 100%;
  }
`;
