import styled from "styled-components";

export const Container = styled.div`
  min-width: 300px;
  max-width: 300px;
  height: auto;
  display: flex;
  flex-direction: column;
  border-radius: 2px;
  align-items: flex-start;
  word-break: break-all;
  cursor: pointer;

  @media (max-width: 900px) {
    min-width: 250px;
    max-width: 250px;
  }

  @media (max-width: 700px) {
    min-width: 200px;
    max-width: 200px;
  }

  img {
    width: 100%;
    max-height: auto;
    margin: 0 auto;
    border-radius: 2px;
    border-bottom: 1px solid rgb(0, 0, 0, 0.1);
  }
`;

export const ProductInfo = styled.div`
  display: flex;
  flex-direction: column;
  overflow: hidden;
  margin-top: 7px;
  width: 100%;

  sub p {
    font-weight: 500;
  }
`;

export const PriceInfo = styled.div`
margin-top: 10px;
`;

export const AboutInfo = styled.div`
  width: 70%;

  div {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  div a {
    font-size: 0.9rem;
    font-weight: 900;
    text-transform: capitalize;
    cursor: pointer;
  }

  sub p {
    font-size: 9pt;
    color: gray;
    margin: 5px 0;
    text-transform: uppercase;
  }
`;
