import React, { useEffect, useState } from "react";
import { BsGeoAlt } from "../../styles/Icons";
import {
  Container,
  Column,
  LocationInfo,
  SearchWrapper,
  SearchTitle,
  SearchBarContent,
  SearchIcon,
  SearchBar,
  Row
} from "./styles";

function Header() {
  const [city, getCity] = useState({});

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(function (position) {
      fetch(
        `http://api.positionstack.com/v1/forward?access_key=67dca1793150fc082092dbe9dbc589a1&query=${position.coords.latitude},${position.coords.longitude}&output=json`
      ).then((response) => {
        getCity(response.data);
      });
    });
  }, []);

  return (
    <Container>
      <Column>
        <h2>Mercado Fechado</h2>
        <LocationInfo>
          <BsGeoAlt size={25} />
          <div>
            <p>Enviar para</p>
            <h3>Uberaba 38082-221</h3>
          </div>
        </LocationInfo>
      </Column>
      <SearchWrapper>
        <SearchBar placeholder="Busque por produtos aqui..." />
        <div className="separator"></div>
        <SearchIcon />
      </SearchWrapper>
      <Row>
        <UserInfo>
          <p>Ol√° {}</p>
          <strong>Sua conta</strong>
        </UserInfo>
      </Row>
    </Container>
  );
}

export default Header;
