import styled from "styled-components";

export const Container = styled.div``;
export const CartContainer = styled.div`
  width: 90%;
  display: flex;
  flex-direction: row;

  img {
    width: 80px;
    height: 80px;
    flex: 1;
  }
`;

export const Info = styled.div`
  display: flex;
  flex-direction: column;
`;
