import React, { useState } from "react";
import { registerUser } from "../../actions/user_actions";
import { useDispatch } from "react-redux";
import {
  Container,
  RegisterWrapper,
  RegisterInput,
  RegisterButton,
} from "./styles";

function UserRegister() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [cep, setCEP] = useState(0);
  const dispatch = useDispatch();

  return (
    <Container>
      <RegisterWrapper>
        <p>Seu nome</p>
        <RegisterInput type="text" onChange={(e) => setName(e.target.value)} />
        <p>Seu email</p>
        <RegisterInput type="text" onChange={(e) => setEmail(e.target.value)} />
        <p>Sua senha</p>
        <RegisterInput
          type="password"
          onChange={(e) => setPassword(e.target.value)}
        />
        <p>Seu CEP</p>
        <RegisterInput type="text" onChange={(e) => setCEP(e.target.value)} />
        <RegisterButton
          type="button"
          value="Criar conta"
          onClick={() => dispatch(registerUser(name, email, password, cep))}
        />
      </RegisterWrapper>
    </Container>
  );
}

export default UserRegister;
