import React, { useState } from "react";
import {
  Container,
  RegisterWrapper,
  RegisterInput,
  RegisterButton,
  DropContainer,
  UploadMessage,
} from "./styles";
import Dropzone from "react-dropzone";

function UserRegister() {
  const [name, setName] = useState("");
  const [pictureInfo, setPictureInfo] = useState({});
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [cep, setCEP] = useState(0);

  function createUserAccount() {
    fetch("http://localhost:3001/auth/register", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        name: name,
        email: email,
        password: password,
        cep: cep,
      }),
    }).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 404:
          console.log("Usuário não encontrado");
          break;
        case 403:
          console.log("Senha inválida");
          break;
        case 200:
          if (data.token) {
            alert("conta criada com sucesso!");
            localStorage.setItem("access_token", data.token);
            window.location.href = "/";
          }
          break;
      }
    });
  }

  function renderDragMessage(isDragActive, isDragReject) {
    if (!isDragActive) {
      return <UploadMessage>Drag images here...</UploadMessage>;
    }

    if (isDragReject) {
      return <UploadMessage type="error">Invalid file type</UploadMessage>;
    }

    return <UploadMessage type="success">Drop it here</UploadMessage>;
  }

  function handleUpload(file) {
    file.map((item) => {
      setPictureInfo(item);
      console.log(item.preview)
    })
  }

  return (
    <Container>
      <RegisterWrapper>
        <p>Seu nome</p>
        <RegisterInput type="text" onChange={(e) => setName(e.target.value)} />
        <p>Seu email</p>
        <RegisterInput type="text" onChange={(e) => setEmail(e.target.value)} />
        <p>Sua senha</p>
        <RegisterInput
          type="password"
          onChange={(e) => setPassword(e.target.value)}
        />
        <p>Seu CEP</p>
        <RegisterInput type="text" onChange={(e) => setCEP(e.target.value)} />
        <Dropzone accept="image/*" onDropAccepted={handleUpload}>
          {({ getRootProps, getInputProps, isDragActive, isDragReject }) => (
            <DropContainer
              {...getRootProps()}
              isDragActive={isDragActive}
              isDragReject={isDragReject}
            >
              <input {...getInputProps()} />
              <img src={pictureInfo.preview} width={25} />
              {renderDragMessage(isDragActive, isDragReject)}
            </DropContainer>
          )}
        </Dropzone>
        <RegisterButton
          type="button"
          value="Criar conta"
          onClick={() => createUserAccount()}
        />
      </RegisterWrapper>
    </Container>
  );
}

export default UserRegister;
