import styled from "styled-components";

export const Container = styled.div``;
export const ProductList = styled.div`
  display: flex;
  justify-content: center;
  max-width: 100%;
  margin: 0 auto;

  ul {
    list-style: none;
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    max-width: 90%;

    li {
      margin: 10px;
    }
  }
`;
