import { createStore } from "redux";

const INITIAL_STATE = {
  user_data: {
    logged_in: false,
    username: "",
    email: "",
    picture:
      "https://preview.redd.it/bp9ty87opis51.jpg?auto=webp&s=57543c51709605fd3738664d99a63eceffa139c9",
  },
};

function user_reducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case "LOGIN":
      return {
        ...state,
        user_data: {
          logged_in: true,
          username: action.username,
          email: action.email,
        },
      };
    default:
      return state;
  }
}

const store = createStore(user_reducer);

export default store;
