import {createStore} from 'redux'

const INITIAL_STATE = {
    user_data: {
        logged_in: false,
        username: 'v0lpt',
    }
}

function user_reducer(state = INITIAL_STATE, action) {
    switch (action.type) {
        case 'LOGIN':
            return {...state, user_data: {logged_in: true, username: action.username}}
        default:
            return state;
    }
}

const store = createStore(user_reducer)

export default store;
