import React, { useEffect, useState } from "react";
import { BsGeoAlt } from "../../styles/Icons";
import { Container, Column, LocationInfo } from "./styles";

function Header() {
  const [city, getCity] = useState({});

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(function (position) {
      fetch(
        `http://api.positionstack.com/v1/forward?access_key=67dca1793150fc082092dbe9dbc589a1&query=${position.coords.latitude},${position.coords.longitude}`
      )
        .then((res) => res.json())
        .then((response) => {
            getCity(response.data);
        });
    });
  }, []);

  alert(city)
  return (

    <Container>
      <Column>
        <h2>Mercado Fechado</h2>
        <LocationInfo>
          <BsGeoAlt size={25} />
          <div>
            <p>Enviar para</p>
            <h3>{city.region}</h3>
          </div>
        </LocationInfo>
      </Column>
    </Container>
  );
}

export default Header;
