import styled from "styled-components";
import { BsSearch } from "../../styles/Icons";

export const Container = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  height: 80px;
  width: 100%;
  padding: 15px 50px;
  background-color: #d7263d;
  color: #FFFFFF;
`;
export const Column = styled.div`
  display: flex;
  height: 100%;
  flex-direction: column;
  justify-content: space-between;
`;
export const LocationInfo = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;

  div p {
    font-size: 9pt;
  }

  h3 {
    font-size: 10pt;
  }
`;

export const SearchWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 600px;

  .separator {
    position: relative;
    z-index: 1;
    width: 2px;
    height: 25px;
    left: -50px;
    background-color: rgba(0, 0, 0, 0.2);
  }
`;
export const SearchIcon = styled(BsSearch)`
  height: 20px;
  width: 20px;
  fill: rgba(0, 0, 0, 0.5);
  cursor: pointer;
`;
export const SearchBar = styled.input`
  background-color: white;
  padding: 10px;
  width: 100%;
  outline: none;
  border: 0;
  border-radius: 5px;

  ~ svg {
    position: relative;
    left: -35px;
    z-index: 1;
  }
`;

export const Row = styled.div`
  display: flex;
  flex-direction: row;
`;

export const UserInfo = styled.div`
  display: flex;
  flex-direction: column;

  div {
    display: flex;
    align-items: center;
    cursor: pointer;
  }
`;
