        />
        <p>Senha</p>
        <LoginInput
          type="password"
          onChange={(e) => setUserData({ password: e.target.value })}
        />
        <LoginInput
          type="button"
          value="Entrar"
          onClick={() => authenticateUser()}
        />
      </LoginWrapper>
    </Container>
  );
}

export default UserLogin;
